
declare namespace AllureReporter {
    function addFeature(
        featureName: string
    ): undefined;
    function addSeverity(
        severity: string
    ): undefined;
    function addIssue(
        issue: string
    ): undefined;
    function addTestId(
        testId: string
    ): undefined;
    function addStory(
        storyName: string
    ): undefined;
    function addEnvironment(
        name: string,
        value: string
    ): undefined;
    function addDescription(
        description: string,
        type: string
    ): undefined;
    function addAttachment(
        name: string,
        content: string,
        type?: string
    ): undefined;
    function addStep(
        title: string,
        attachmentObject?: object,
        status?: string
    ): undefined;
    function addArgument(
        name: string,
        value: string
    ): undefined;
}

declare module "@wdio/allure-reporter" {
    export default AllureReporter
}
